import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './Banner.css';

export const Banner = () => {
  const [showDatePicker, setShowDatePicker] = useState(false);

  const toggleDatePicker = () => {
    setShowDatePicker((prev) => !prev);
  };

  return (
    <section className="banner">
      <div className="banner-background"></div>
      <div className="banner-content">
        <img className="banner-image" src="/images/banner.png" alt="banner Champions" />
        <div className="banner-text">Live Football</div>
        <div className="banner-button-container">
          <button className="banner-button" id="live_picker_button">
            LIVE
          </button>
          <button className="banner-button" id="date_picker_button" onClick={toggleDatePicker}>
            DATE
          </button>
        </div>
        {/* Renderize o DatePicker somente se showDatePicker for verdadeiro */}
        {showDatePicker && (
          <DatePicker
            selected={new Date()}
            onChange={(date) => console.log(date)}
            dateFormat="d/MM/yyyy"
          />
        )}
      </div>
    </section>
  );
};
