import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'; 
import Banner from './components/Banner';
import GamesBody from './components/GamesBody';


function App() {
  const [showTemporaryDatePicker, setShowTemporaryDatePicker] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showButton, setShowButton] = useState(true);

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setShowTemporaryDatePicker(false);
    setShowButton(true);
  };

  const toggleDatePicker = () => {
    setShowTemporaryDatePicker((prev) => !prev);
    setShowButton(false);
  };


  return (
    <div className="App">
      <Banner
        showTemporaryDatePicker={showTemporaryDatePicker}
        toggleDatePicker={toggleDatePicker}
        showButton={showButton}
      />
      {showTemporaryDatePicker && (
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          dateFormat="d/MM/yyyy"
        />
      )}
      <GamesBody selectedDate={selectedDate.toLocaleDateString() + " Games"} />
    </div>
  );
}

export default App;
