import React from 'react'
import LeagueCard from '../LeagueCard'; 
import './GamesBody.css'

export const GamesBody = (props) => {

  const fixturesByLeague = {};
  const fixturesByLeagueByDate = {};

  // Group fixtures by league
  props.fixtures.forEach((fixture) => {
    const leagueId = fixture.league.id;
    if (!fixturesByLeague[leagueId]) {
      fixturesByLeague[leagueId] = [];
    }
    fixturesByLeague[leagueId].push(fixture);
  });

   // Group fixtures by league by Date
   Object.values(fixturesByLeague).forEach((leagueFixtures) => {
    leagueFixtures.forEach((fixture) => {
      if (fixture.date === props.selectedDate) {
        fixturesByLeagueByDate.push(fixture);
      }
    });
  });
  console.log('FixturebyLeague -',fixturesByLeague)
  console.log('fixturesByLeagueByDate - ',fixturesByLeagueByDate)

  const leagueCards = Object.entries(fixturesByLeague).map(([leagueId, leagueFixtures]) => (
    <LeagueCard
      key={leagueId}
      leagueId={leagueId}
      leagueFixtures={leagueFixtures}
    />
  ));

  return (
    <section className='main-container' id='mainContainer'>
      <p className='title-body' id='selectedDate'>{props.selectedDate}</p>
      {leagueCards}
    </section>
  );
}
